import React from 'react';
import img from '../images/Basket/01.jpg';
import styled from 'styled-components';

const imgSize = styled.div`
    width : 300px;
    height : 600px;
    padding :  100px 50px 100px 100px;
    @media screen and (max-width: 768px) {
        width: 100%;
        overflow-x: auto;
      }
`;


const teBox = styled.div`
    width : 40%;
    height : 600px;
    padding : 100px 50px 100px 100px;,
    color : gray;
    weight : bold;
    fontSize : 40px;
`;

const textBox = styled.div``;

const btn1 = {
    background: 'white',
    border : '2px solid #edc13b',
    width : '300px',
    height : '50px',
    margin : '50px 0'
}

const btn2 = {
    background: 'white',
    border : '2px solid #edc13b',
    width : '300px',
    height : '50px',
    float : 'right',
    margin : '50px 0'
}

const Basket1 = () => {
    return (
        <>
        <imgSize>
            <img src={img} alt="사진" style={imgSize} />
        </imgSize>

        <teBox >
            <p>이름 : 데모르</p>
            <p>가격 : 10,000원</p>
            <hr></hr>
            <p>3천원 이상 구매시 무료배송</p>
            <hr></hr>
            <p>수량 : </p>
            <p>편지추가 : 데모르</p>
            </teBox>
            <div>
            <button style={btn1}> 장바구니 </button>
            <button style={btn2}> 구매하기 </button>
            </div>
      
        
        </>
    );
};

export default Basket1;
