import React from 'react';
import { Button, Descriptions } from 'antd';
import Axios from 'axios';
import { useDispatch } from 'react-redux';
import {addToCart} from '../../../../_actions/user_actions';


function Productinfo(props) {
    const dispatch = useDispatch();

    const clickHander = () => {

     // 필요한 정보를 Cart 필드에 넣어 준다.
        dispatch(addToCart(props.detail._id))
            Axios
        
    }

    
    return (
        <>
        <div>
            <Descriptions title="데모르" bordered>
            <Descriptions.Item label="가격">15,000원</Descriptions.Item>
            <Descriptions.Item label="색깔">노랑톤</Descriptions.Item>
            <Descriptions.Item label="물 주기">일주일에 한번 필수</Descriptions.Item>
            <Descriptions.Item label="개화시기">4월 ~ 6월</Descriptions.Item>
   
  </Descriptions>
        </div>
        <div>
            <hr />
            <p 
            style={{ padding: '30px', fontWeight: 'bold', fontSize: '20px', margin: '0 auto'   }}>3만원 이상 구매시 무료배송</p>
            <hr />
            
             <p
              style={{ padding: '30px', fontWeight: 'bold', fontSize: '20px', margin: '0 auto'   }}
             >수량 : </p> 
        </div>

        <br />
        <br />
        <br />
        <div style={{ display: 'flex', justifyContent: 'center'}}>
            <Button size="large" shape="round" type="danger" onClick={clickHander} > 
                장바구니
            </Button>

        </div>
        </>
    )
}

export default Productinfo;
